import React, { useState, useEffect } from "react";
import axios from "axios";

const Response = () => {
  const [responses, setResponses] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:5000/api/contact")
      .then((res) => setResponses(res.data))
      .catch((err) => console.error("Error fetching responses", err));
  }, []);

  return (
    <div className="max-w-7xl mx-auto p-4 md:p-6">
      <h2 className="text-2xl md:text-3xl font-semibold text-[#0B3159] text-center mb-4">
        All Responses
      </h2>

      <div className="overflow-x-auto">
        <table className="w-full border-collapse border border-gray-300 bg-white shadow-lg text-sm md:text-base">
          <thead className="bg-[#0B3159] text-white text-left">
            <tr className="text-xs md:text-sm">
              <th className="border p-2 md:p-3">Name</th>
              <th className="border p-2 md:p-3">Email</th>
              <th className="border p-2 md:p-3">Service</th>
              <th className="border p-2 md:p-3">Message</th>
              <th className="border p-2 md:p-3">Date/Time</th>
            </tr>
          </thead>
          <tbody>
            {responses.length > 0 ? (
              responses.map((response, index) => (
                <tr
                  key={response._id}
                  className={`text-center ${
                    index % 2 === 0 ? "bg-gray-100" : "bg-white"
                  }`}
                >
                  <td className="border p-2 md:p-3">{response.name}</td>
                  <td className="border p-2 md:p-3">{response.email}</td>
                  <td className="border p-2 md:p-3">{response.service}</td>
                  <td className="border p-2 md:p-3 max-w-xs overflow-x-auto">
                    <div className="whitespace-nowrap overflow-x-auto scrollbar-thin scrollbar-thumb-gray-400 scrollbar-track-gray-200 p-1">
                      {response.message}
                    </div>
                  </td>
                  <td className="border p-2 md:p-3">
                    {new Date(response.createdAt).toLocaleString()}
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td
                  colSpan="5"
                  className="border p-3 text-center text-gray-500 bg-gray-100"
                >
                  No response yet
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Response;
