import React, { useState, useEffect } from "react";
import axios from "axios";
import HeaderAdmin from "./HeaderAdmin";
import Footer from "../components/Footer";

const Settings = () => {
  const [admin, setAdmin] = useState({ email: "", password: "" });
  const [newPassword, setNewPassword] = useState("");

  useEffect(() => {
    // Fetch the logged-in admin details from localStorage or API
    const storedAdmin = JSON.parse(localStorage.getItem("admin"));
    if (storedAdmin) {
      setAdmin({ email: storedAdmin.email, password: storedAdmin.password });
    }
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const res = await axios.put("http://localhost:5000/api/admin/update-password", {
        email: admin.email,
        password: admin.password,
        newPassword,
      });

      if (res.status === 200) {
        alert("Password is updated.");
        setNewPassword("");

        // Update stored admin details with new password
        localStorage.setItem("admin", JSON.stringify({ ...admin, password: newPassword }));
      }
    } catch (error) {
      console.error("Error updating password:", error);
      alert("Failed to update password.");
    }
  };

  return (
    <>
      <HeaderAdmin />
      <div className="max-w-3xl mx-auto p-6">
        <h2 className="text-3xl font-semibold text-[#0B3159] text-center mb-6">
          Change Password
        </h2>

        <form onSubmit={handleSubmit} className="bg-white shadow-lg rounded-lg p-6 border">
          <div className="mb-4">
            <label className="block text-[#0B3159] text-sm font-semibold mb-2">
              Email
            </label>
            <input
              type="text"
              value={admin.email}
              readOnly
              className="w-full px-4 py-2 border rounded-lg bg-gray-100 text-gray-700"
            />
          </div>

          <div className="mb-4">
            <label className="block text-[#0B3159] text-sm font-semibold mb-2">
              Current Password
            </label>
            <input
              type="password"
              value={admin.password}
              readOnly
              className="w-full px-4 py-2 border rounded-lg bg-gray-100 text-gray-700"
            />
          </div>

          <div className="mb-4">
            <label className="block text-[#0B3159] text-sm font-semibold mb-2">
              New Password
            </label>
            <input
              type="password"
              value={newPassword}
              onChange={(e) => setNewPassword(e.target.value)}
              required
              className="w-full px-4 py-2 border rounded-lg focus:ring-2 focus:ring-[#E5A24A]"
            />
          </div>

          <div className="text-center">
            <button
              type="submit"
              className="bg-[#E5A24A] hover:bg-[#c7873a] text-white font-semibold py-2 px-6 rounded-lg shadow-md transition duration-300"
            >
              Update Password
            </button>
          </div>
        </form>
      </div>
      <Footer />
    </>
  );
};

export default Settings;
